import "./ContainerCard.css";
import CardGames from "../CardGames/CardGames";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { indexPage } from "../../redux/actions/actions";
import { PuffLoader } from "react-spinners";

const ContainerCard = () => {
  const games = useSelector((state) => state.Games);
  const search = useSelector((state) => state.searchInput);
  const [isloading, setIsloading] = useState(true);

  let indexpages= 1;
  const dispatch = useDispatch();
  const [sort, setSort] = useState("");
  const [index, setCountIndex] = useState(indexpages);

  const handleClick = (e) => {
    e.preventDefault();

    setSort(e.target.value);
  };

  useEffect(() => {
    setIsloading(true);
    dispatch(indexPage(index)).then((game) => setIsloading(false));
    // eslint-disable-next-line
  }, [sort, search, index]);

  return (
    <div className="ContainerCard">
      <div className="containerfilters">
        <div className="iconfilter">
          <ion-icon size="large" name="filter-outline"></ion-icon>
        </div>
        <div className="gender">
          <select name="gender" id="gender">
            <option value="">Seleciona</option>
            <option value="shooter">Shooter</option>
            <option value="action">Action</option>
          </select>
        </div>
        <button className="apllyfilters">Aplicar Fitlros</button>
        <div className="ascanddesc">
          <select name="sort" id="sort" onClick={handleClick}>
            <option value="ACS">A a Z</option>
            <option value="DESC">Z a A</option>
          </select>
        </div>

        <h1 className="TitlleGame">
          Popular<span> Games</span>
        </h1>

        <div className="pageNext">
          <button onClick={() => setCountIndex(index - 1)}>{`<`}</button>
          <span> {index} </span>
          <button onClick={() => setCountIndex(index + 1)}>{`>`}</button>
        </div>
      </div>
      {!isloading ? (
        <div className="cardcontainerView">
          {(search && search.length > 0 ? search : games)
            .slice()
            .sort((a, b) => {
              const A = a.name.localeCompare(b.name)
              const B = b.name.localeCompare(a.name)
              if (sort === "ACS") {
                return B
              } else {
                return A
              }
            })
            .map((game) => (
              <CardGames key={game.id} game={game} />
            ))}
        </div>
      ) : (
        <div className="loading">
          <PuffLoader color="#cf4784" size={100} />
        </div>
      )}
    </div>
  );
};

export default ContainerCard;
