//Types
import {
    GET_GAMES, SEARCH_GAME, SEARCH_GAME_GENRES
} from '../actions/actions'

const initialState = {
    Games: [],
    GameDetails: {},
    Favorites:[],
    genres: [],
    filterOptions: {
    searchInput: '',
    genre: '',
  },
}

const RootReducer = (state = initialState, action) => {
    switch (action.type) {
         case GET_GAMES:
            return {
                ...state,
                Games : action.payload
            }   
          case SEARCH_GAME:
            return{
                ...state,
                filterOptions: {
                    ...state.filterOptions,
                   searchInput: action.payload.searchInput,
                  }
            }  
            case SEARCH_GAME_GENRES:
                return {
                    ...state,
                    filterOptions: {
                        ...state.filterOptions,
                        genre: action.payload
                      }
                }
        default:
            return state;
    }
}

export default RootReducer;
