import axios from "axios";

/* Types */

export const GET_GAMES = "GET_GAMES";
export const SEARCH_GAME = "SEARCH_GAME";
export const SEARCH_GAME_GENRES = "SEARCH_GAME_GENRES";
export const GAME_DETAILS = "GAME_DETAILS";
export const INDEX_PAGE = "INDEX_PAGE";
/* Types */

// Functions
/* Get all Games */

export const getGames = (index) => {
  return async function (dispatch) {
    const res = await axios.get("http://localhost:3001/games", index);
    return dispatch({
      type: GET_GAMES,
      payload: res.data,
    });
  };
};

/* Get all Games */

/* SearchGame */
export const searchGame = (searchInput) => {
  return async function (dispatch) {
    const res = await axios.get(
      `http://localhost:3001/games/videogames/name?name=${searchInput}`
    );


    return dispatch({
      type: SEARCH_GAME,
      payload: res.data,
    });
  };
};

/* SearchGame */

/* DETAILS GAME */

export const gameDetail = (id) => {
  return async function (dispatch) {
    const res = await axios.get(`http://localhost:3001/games/${id}`);

    return dispatch({
      type: GAME_DETAILS,
      payload: res.data,
    });
  };
};
/* DETAILS GAME */


export const indexPage = (index) => {
  return async function (dispatch) {
    

    return dispatch({
      type: INDEX_PAGE,
      payload: index
    });
  };
};
