import "./Sidebar.css";
import image from '../../assest/zgamer.png';
import { Link } from 'react-router-dom'
import Modal from 'react-modal';
import { useState } from "react";

const Sidebar = () => {

  const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
    },
  };

  const [modalIsOpen, setIsOpen] = useState(false);

  function openModal() {
    setIsOpen(true);
  }


  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div className="sidebarcontainer">
      <div className="top">
        <div className="logo">
          <Link to={'/'}>
          <img src={image} alt="" />
          </Link>
        </div>
      </div>
      <div className="center">
        <div className="containersidebarcenter">
          <ul className="ulsidebarmenu">
            <div className="active">
              <li>
                <ion-icon size="large" name="game-controller-outline" />
                Games
              </li>
            </div>
            <div className="">
              <li>
                <ion-icon size="large" name="bookmarks-outline" />
                Favorites
              </li>
            </div>
            <div className="">
              <li onClick={ () => setIsOpen(true)}>
                <ion-icon size="large" name="add-circle-outline" />
                Add Games
              </li>
            </div>
          </ul>
         
        </div>
      </div>
      <div className="bottom">
      <Modal isOpen = { modalIsOpen } 
 
        onRequestClose = { closeModal } 
        style = { customStyles } 
        contentLabel = "Ejemplo Modal" >
      
      </Modal>
       
    
      </div>
    </div>
  );
};

export default Sidebar;
