import "./Sidebar.css";
import image from "../../assest/zgamer.png";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { createvideoGames } from "../../redux/actions/actions";

const Sidebar = () => {
  const dispatch = useDispatch();

  const [newGame, setNewGame] = useState({
    name: "",
    desc: "",
    platforms: [],
    image: "",
    release: "",
    rating: 0,
  });

  const handleChange = (e) => {
    setNewGame({
      ...newGame,
      platforms: [e.target.value],
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(createvideoGames(newGame));
    setIsOpen(false);
  };

  const customStyles = {
    overlay: {
      backgroundColor: "#181a2694",
    },
    content: {
      top: "50%",
      left: "50%",
      right: "auto",
      bottom: "auto",
      marginRight: "-50%",
      transform: "translate(-50%, -50%)",
      backgroundColor: " rgb(77, 72, 72)",
    },
  };

  const [modalIsOpen, setIsOpen] = useState(false);

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div className="sidebarcontainer">
      <div className="top">
        <div className="logo">
          <Link to={"/"}>
            <img src={image} alt="" />
          </Link>
        </div>
      </div>
      <div className="center">
        <div className="containersidebarcenter">
          <ul className="ulsidebarmenu">
            <div className="active">
              <li>
                <ion-icon size="large" name="game-controller-outline" />
                Games
              </li>
            </div>

            <div className="">
              <li onClick={() => setIsOpen(true)}>
                <ion-icon size="large" name="add-circle-outline" />
                Add Games
              </li>
            </div>
          </ul>
        </div>
      </div>
      <div className="bottom">
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Ejemplo Modal"
        >
          <form onSubmit={handleSubmit}>
            <h1>Create Game</h1>
            <div className="nameGame">
              <label htmlFor="name">Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                onChange={handleChange}
              />
            </div>
            <div className="descGame">
              <label htmlFor="desc">Desciption:</label>
              <textarea
                type="text"
                id="desc"
                name="desc"
                onChange={handleChange}
              />
            </div>
            <div className="platformsGame">
              <label htmlFor="platforms">Plataformas</label>
              <input
                type="text"
                id="platforms"
                name="platforms"
                onChange={handleChange}
              />
            </div>
            <div className="imageGame">
              <label htmlFor="image">Image:</label>
              <input
                type="text"
                id="image"
                name="image"
                onChange={handleChange}
              />
            </div>
            <div className="releaseGame">
              <label htmlFor="name">Release:</label>
              <input
                type="date"
                id="release"
                name="release"
                onChange={handleChange}
              />
            </div>
            <div className="ratingGame">
              <label htmlFor="rating">Rating:</label>
              <input
                type="number"
                id="rating"
                name="rating"
                onChange={handleChange}
              />
            </div>
            <button type="submit">Agregar Juego</button>
          </form>
        </Modal>
      </div>
    </div>
  );
};

export default Sidebar;
