import "./ContainerCard.css";
import CardGames from "../CardGames/CardGames";
import { useSelector } from "react-redux";

const ContainerCard = () => {
  const games = useSelector((state) => state.Games);
  const search = useSelector((state) => state.filterOptions.searchInput);
  const genre = useSelector((state) => state.filterOptions.genre);
  
  console.log("search", search);
  return (
    <div className="ContainerCard">
      <div className="containerfilters">
        <div className="iconfilter">
          <ion-icon size="large" name="filter-outline"></ion-icon>
        </div>
        <div className="gender">
          <select name="gender" id="gender">
            <option value="Gender">Gender</option>
          </select>
        </div>
        <div className="ascanddesc">
          <select name="sort" id="sort">
            <option value="ACS">ACS</option>
            <option value="DESC">DESC</option>
          </select>
        </div>
        <h1 className="TitlleGame">
          Popular<span> Games</span>
        </h1>
      </div>
      <div className="cardcontainerView">
        {search.length !== 0 ||
        search.trim() === "" 
          ? games
              ?.filter((game) => {
                const matchSearch = game.name
                  .toLowerCase()
                  .includes(search.toLowerCase());
                const matchGenre = game.genres.some(
                  (genre) =>
                    genre.name.toLowerCase() === genre.toLowerCase()
                );
                return matchSearch && matchGenre;
              })
              .map((game) => <CardGames key={game.id} game={game} />)
          : games?.map((game) => <CardGames key={game.id} game={game} />)}
      </div>
    </div>
  );
};

export default ContainerCard;
