import { useParams } from "react-router-dom";
import "./GameDetails.css";
import { useEffect, useState } from "react";
import { gameDetail } from "../../redux/actions/actions";
import { useDispatch, useSelector } from "react-redux";
import { PuffLoader } from "react-spinners";

const GameDetails = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const game = useSelector((state) => state.GameDetails);
  const [isloading, setIsLoading] = useState(true);

  useEffect(() => {
    dispatch(gameDetail(id)).then((dispatch) => setIsLoading(false));
    // eslint-disable-next-line
  }, [id]);
  return (
    <>
      {isloading ? (
        <div className="loading">
          <PuffLoader color="#cf4784" size={100} />
        </div>
      ) : (
        <div className="containerDetails">
          <div className="topdetail">
            <div className="leftdetails">
              <h1>{game.name}</h1>
              <p>{game.description_raw}</p>
              <div className="incosrsssdetails">
                <ion-icon size="large" name="logo-xbox"></ion-icon>
                <ion-icon size="large" name="desktop-outline"></ion-icon>
                <ion-icon size="large" name="logo-playstation"></ion-icon>
              </div>
              <p>Fecha de Lanzamiento: {game.released}</p>
              <p>Rating: {game.rating}</p>
              {game.genres ? (
                <div className="parrafosrow">
                  <h1>Generos:</h1>
                  {game.genres?.map((p) => (
                    <div className="genresRow" key={p.id}>
                      {p.name}
                    </div>
                  ))}
                </div>
              ) : null}
            </div>
            <div className="rightdetails">
              {game.background_image ? (
                <img src={game.background_image || "https://www.online-tech-tips.com/wp-content/uploads/2022/11/preview-media-01-Title-Image-1.jpeg" } alt="" />
              ) : (
                <img
                  src="https://www.online-tech-tips.com/wp-content/uploads/2022/11/preview-media-01-Title-Image-1.jpeg"
                  alt=""
                />
              )}
            </div>
          </div>
          <div className="bottomdetails"></div>
        </div>
      )}
    </>
  );
};

export default GameDetails;
