import "./ContainerCard.css";
import CardGames from "../CardGames/CardGames";
import { useSelector } from "react-redux";
import { useState } from "react";

const ContainerCard = () => {
  const games = useSelector((state) => state.Games);
  const search = useSelector((state) => state.searchInput);

  const [sort, setSort] = useState("");
 
  const [numPage, setnumPage] = useState(15);
  const [indexpage, setIndexpage] = useState(1);

  const indiceInicio = (indexpage - 1) * numPage;
  const indiceFin = indiceInicio + numPage;

  const handleClick = (e) => {
    e.preventDefault();

    setSort(e.target.value);
  };


  const handlePage = (page) => {
    

    if(page === "next"){
      return indexpage + 1
    }else{
      return indexpage - 1
    }
  }
  return (
    <div className="ContainerCard">
      <div className="containerfilters">
        <div className="containerfilterall">
          <div className="iconfilter">
            <ion-icon size="large" name="filter-outline"></ion-icon>
          </div>

          <div className="sort">
            <p>Ordenamiento:</p>
            <div className="ascanddesc">
              <select name="sort" id="sort" onClick={handleClick}>
                <option value="">Selecione</option>
                <option value="ACS">A a Z</option>
                <option value="DESC">Z a A</option>
              </select>
            </div>
          </div>

          <h1 className="TitlleGame">
            Popular<span> Games</span>
          </h1>
        </div>

        <div className="pageNext">
          <button className="flechaspages" onClick={ handlePage('next')}>{`<`}</button>
          <span> {indexpage}</span>
          <button className="flechaspages"  onClick={handlePage}>{`>`}</button>
        </div>
      </div>

      <div className="cardcontainerView">
        {search && search.length > 0
          ? search
              .sort((a, b) => {
                if (sort === "ACS") {
                 return b.name.localeCompare(a.name);
               } else  {
                  return a.name.localeCompare(b.name);
               }
              })
              .map((game, i) => <CardGames key={i} game={game} />)
              .slice(indiceInicio,indiceFin )
          : games
              .sort((a, b) => {
                if (sort === "ASC") {
                  return a.name.localeCompare(b.name);
                } else if(sort === "DESC") {
                  return b.name.localeCompare(a.name);
                }else{
                  return a.id - b.id;
                }
              })
              .map((game, i) => <CardGames key={i} game={game} />)
              .slice(indiceInicio,indiceFin )
              }
      </div>
    </div>
  );
};

export default ContainerCard;
