import "./Sidebar.css";
import image from "../../assest/zgamer.png";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { createvideoGames } from "../../redux/actions/actions";
import {Validation} from "../../helpers/Validation/Validation";
import Form from "../Form/Form";

const Sidebar = () => {
  const dispatch = useDispatch();
  const [validation, setValidation] = useState('');
  
  const [newGame, setNewGame] = useState({
    name: "",
    description_raw: "",
    platforms: [],
    background_image: "",
    released: "",
    rating: 0,
  });
  
  
  const handleChange = (e) => {
    setNewGame({
      ...newGame,
      [e.target.name]: e.target.value,
    });
  };

  const handleCheckboxChange = (e) => {
    const value = e.target.value.split(" ");
    const isCheked = e.target.checked;

    if (isCheked) {
      setNewGame({ ...newGame, platforms: [...newGame.platforms, ...value] });
    } else {
      setNewGame({
        ...newGame,
        platforms: newGame.platforms.filter((p) => !p.includes(value)),
      });
    }
  };

  

  const customStyles = {
    overlay: {
      backgroundColor: "#181a2694",
    },
    content: {
      top: "50%",
      left: "50%",
      right: "auto",
      bottom: "auto",
      marginRight: "-50%",
      transform: "translate(-50%, -50%)",
      backgroundColor: " rgb(77, 72, 72)",
      width: "1200px",
      display: "flex",
      border: "none",
      backgroundImage:
        "url('https://static.13.cl/7/sites/default/files/images/ocupload/serie-the-last-of-us-hbo.jpg')",
      backgroundSize: "cover",
    },
  };

  const [modalIsOpen, setIsOpen] = useState(false);

  function closeModal() {
    setIsOpen(false);
    setNewGame({
      name: "",
      description_raw: "",
      platforms: [],
      background_image: "",
      released: "",
      rating: 0,
    });
  }

  return (
    <div className="sidebarcontainer">
      <div className="top">
        <div className="logo">
          <Link to={"/"}>
            <img src={image} alt="" />
          </Link>
        </div>
      </div>
      <div className="center">
        <div className="containersidebarcenter">
          <ul className="ulsidebarmenu">
            <div className="active">
              <li>
                <ion-icon size="large" name="game-controller-outline" />
                Games
              </li>
            </div>

            <div className="">
              <li onClick={() => setIsOpen(true)}>
                <ion-icon size="large" name="add-circle-outline" />
                Add Games
              </li>
            </div>
          </ul>
        </div>
      </div>
      <div className="bottom">
        <Modal
          ariaHideApp={false}
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Ejemplo Modal"
        >
          <div className="left">
            <img src={image} alt="" />
          </div>
          <div className="right">
            <h2
              onClick={() => {
                closeModal();
              
              }}
            >
              X
            </h2>
           <Form setIsOpen={setIsOpen}/>
          </div>
        </Modal>
      </div>
    </div>
  );
};

export default Sidebar;
