//Types
import {
    GET_GAMES, SEARCH_GAME
} from '../actions/actions'

const initialState = {
    Games: [],
    GameDetails: {},
    Favorites:[],
    genres: [],
    filterOptions: {
    searchInput: '',
    genre: 'action',
  },
}

const RootReducer = (state = initialState, action) => {
    switch (action.type) {
         case GET_GAMES:
            return {
                ...state,
                Games : action.payload
            }   
          case SEARCH_GAME:
            return{
                ...state,
                filterOptions: {
                   searchInput: action.payload.searchInput,
                   genre: action.payload.genre,
                  }
            }  

        default:
            return state;
    }
}

export default RootReducer;
