import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { getGenresall } from "../../redux/actions/actions";

const GenresViews = ({genre}) => {

    const dispatch = useDispatch()
    useEffect(() => {
        dispatch(getGenresall())
    }, []);
  return (
    <div>
      <label>
        <input type="checkbox" name={genre.name} value={genre.name} />
        {genre.name}
      </label>
    </div>
  );
};

export default GenresViews;
