import CardGames from '../../components/CardGames/CardGames';

const Filtered = () => {

    const genresfilter = useSelector((state) => state.filteredOptions.genresFilter);
    const ratingFilter = useSelector((state) => state.filteredOptions.ratingFilter);
    const games = useSelector((state) => state.Games);
    
   
