    }, [genresfilter, ratingFilter]);

  return (
    <div className='cardcontainerView'>
      {
        games?.filter(game=>
          {
            game.genres.includes(gamegenres => gamegenres.name)
          }).map((game, i)=> (
          
          <CardGames key={i} game={game} />
        ))
      }
      </div>
  )
}

export default Filtered
