//Types
import { GET_GAMES, SEARCH_GAME ,GAME_DETAILS, CREATE_GAME, SEARCH_GAME_NOT} from "../actions/actions";

const initialState = {
  Games: [],
  GameDetails: {},
  searchInput: [],
  loading: false, 
};

const RootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_GAMES:
      return {
        ...state,
        Games: action.payload,
      };
    case SEARCH_GAME:
      return {
        ...state,
        searchInput: [  action.payload],
      }
      case GAME_DETAILS:
        return {
          ...state,
          GameDetails : action.payload
        }
        case  CREATE_GAME: 
        return {
          ...state,
          Games: [ action.payload , ...state.Games ]
        }
        case SEARCH_GAME_NOT:
          return {
            ...state,
            searchInput: []
          }
    default:
      return state;
  }
};

export default RootReducer;
