//Types
import {
    GET_GAMES
} from '../actions/actions'

const initialState = {
    Games: [],
    GameDetails: {},
    Favorites:[],
    genres: []
}

const RootReducer = (state = initialState, action) => {
    switch (action.type) {
         case GET_GAMES:
            return {
                ...state,
                Games : action.payload
            }   


        default:
            return state;
    }
}

export default RootReducer;
